    public function serialization ( $dataAX )
    {
      if ( !is_string ( $this->outputCStr ) )
      {
        $this->outputCStr = '';
      }
      
      $dataAX = array ( "serialization" => $dataAX );
      
      if ( is_bool ( $dataAX[ 'serialization' ][ 'entities' ] ) )
      {
        parent::addData ( "serialization.entities", $dataAX[ 'serialization' ][ 'entities' ] );
        unset ( $dataAX[ 'serialization' ][ 'entities' ] );
        parent::garbageCollector ( $dataAX );
      }
      else if ( isset ( $dataAX[ 'serialization' ][ 'entities' ] ) )
      {
        throw new Exception ( "Type error - KendoUiEditor::serialization ( \$data[ 'serialization' ][ 'entities' ] type must be 'Boolean'" );
      }
      
      if ( is_bool ( $dataAX[ 'serialization' ][ 'scripts' ] ) )
      {
        parent::addData ( "serialization.scripts", $dataAX[ 'serialization' ][ 'scripts' ] );
        unset ( $dataAX[ 'serialization' ][ 'scripts' ] );
        parent::garbageCollector ( $dataAX );
      }
      else if ( isset ( $dataAX[ 'serialization' ][ 'scripts' ] ) )
      {
        throw new Exception ( "Type error - KendoUiEditor::serialization ( \$data[ 'serialization' ][ 'scripts' ] type must be 'Boolean'" );
      }
      
      if ( is_bool ( $dataAX[ 'serialization' ][ 'semantic' ] ) )
      {
        parent::addData ( "serialization.semantic", $dataAX[ 'serialization' ][ 'semantic' ] );
        unset ( $dataAX[ 'serialization' ][ 'semantic' ] );
        parent::garbageCollector ( $dataAX );
      }
      else if ( isset ( $dataAX[ 'serialization' ][ 'semantic' ] ) )
      {
        throw new Exception ( "Type error - KendoUiEditor::serialization ( \$data[ 'serialization' ][ 'semantic' ] type must be 'Boolean'" );
      }
      
      if ( is_array ( $dataAX[ 'serialization' ] ) )
      {
        parent::addData ( "serialization", $dataAX[ 'serialization' ] );
        unset ( $dataAX[ 'serialization' ] );
        parent::garbageCollector ( $dataAX );
